tinymce.PluginManager.requireLangPack('readmore');

tinymce.PluginManager.add("readmore", function(editor, url) {
	
	editor.addCommand("insertReadmore", function() {
		
		if(editor.dom.get( 'system-readmore' ) != null)
		{
			alert(tinymce.i18n.translate('readmore.error'));
			return;
		}
		
		editor.insertContent('<div id="system-readmore"></div><p>&nbsp;</p>')
		
	}),
	
	editor.addButton("readmore", {
		icon : "readmore",
		tooltip : 'readmore.insert',
		cmd : "insertReadmore",
		image : url + '/images/readmoreButton.png',
	})
	
});